const combined = [...existing, ...newWords];

// Validate: check for duplicate IDs
const ids = combined.map(w => w.id);
const idSet = new Set(ids);
if (idSet.size !== ids.length) {
  const dupes = ids.filter((id, idx) => ids.indexOf(id) !== idx);
  console.error("DUPLICATE IDS:", dupes);
  process.exit(1);
}

// Validate: check for duplicate bare words (allow if different category)
const bareWords = combined.map(w => w.bare.toLowerCase());
const bareCount = {};
bareWords.forEach(b => { bareCount[b] = (bareCount[b] || 0) + 1; });
const dupeBares = Object.entries(bareCount).filter(([k,v]) => v > 1);
if (dupeBares.length > 0) {
  console.warn("Words appearing more than once (may be OK if different categories):");
  dupeBares.forEach(([word, count]) => {
    const entries = combined.filter(w => w.bare.toLowerCase() === word);
    console.warn(`  "${word}" x${count}: ${entries.map(e => e.id + ' (' + e.category + ')').join(', ')}`);
  });
}

// Total count
console.log("Total words:", combined.length);

